import React, { Component } from "react";

export default class StudentPopup extends Component {
    handleClick = () => {
        this.props.toggle();
    };

    render() {
        return (
            <div className="modal">
                <div className="modal_content">
                    <form className='addStudent' onSubmit={this.onSubmit} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }}>
                        <p>Dodaj nowego studenta</p>

                        <div>
                            <input type="text"
                                placeholder='Imie i nazwisko'
                                value={this.props.studentName || ''}
                                onChange={(e) => this.setState({ name: e.target.value })} />
                        </div>

                        <div>
                            <input type="text"
                                placeholder='Opis'
                                value={this.props.studentDescription || ''}
                                onChange={(e) => this.setState({ description: e.target.value })} />
                        </div>

                        <div>
                            <input type="text"
                                placeholder='Email'
                                value={this.state.mail || ''}
                                onChange={(e) => this.setState({ mail: e.target.value })} />
                        </div>

                        <div className="input-tag">
                            <ul className="input-tag__tags">

                                {this.state.tags.map((tag, i) => (
                                    <li key={tag}>
                                        <button type="button" onClick={() => { this.removeTag(i); }}>{tag}</button>
                                    </li>
                                ))}

                                <li className="input-tag__tags__input">
                                    <input type="text" placeholder='Dodaj tagi' onChange={this.getSuggestions} onKeyDown={this.inputKeyDown} ref={c => { this.tagInput = c; }} />
                                </li>

                            </ul>

                        </div>

                        <div className="input-tag">
                            <ul className="input-tag__tags">
                                {this.state.suggestions.map((tag, i) => (
                                    <li key={tag.uniqueId}>
                                        <button type="button" onClick={() => { this.addFromSugestions(i); }}>{tag}</button>
                                    </li>
                                ))}
                            </ul>
                        </div>
                        <input type='button' className='submitButton' value='Anuluj' />
                        <input type='submit' className='submitButton' value='Zapisz' />
                    </form>
                </div>
            </div>
        );
    }

}
