export default class ReactTag extends Component {

    constructor(props) {
      super(props)

      this.state = {
        tags: [
          { id: 1, name: "Apples" },
          { id: 2, name: "Pears" }
        ],
        suggestions: [
          { id: 3, name: "Bananas" },
          { id: 4, name: "Mangos" },
          { id: 5, name: "Lemons" },
          { id: 6, name: "Apricots" }
        ]
      }
    }

    handleDelete(i) {
      if (i === -1) return false;
      const tags = this.state.tags.slice(0)
      tags.splice(i, 1);
      const removedTag = this.state.tags[i];
      const suggestions = [].concat(this.state.suggestions, removedTag);
      this.setState({ tags, suggestions })
    }

    handleAddition(tag) {
      const tags = [].concat(this.state.tags, tag);
      const suggestions = this.state.suggestions.filter(x => x.id !== tag.id);
      this.setState({ tags, suggestions })
    }

    render() {
      return (
        <ReactTags
          tags={this.state.tags}
          suggestions={this.state.suggestions}
          handleDelete={this.handleDelete.bind(this)}
          handleAddition={this.handleAddition.bind(this)} />
      )
    }

  }
