import React, { Component } from 'react'

export default class SearchFilter extends Component {
  constructor() {
    super();
    this.state = {
      searchTag: 'Wyszukuj po tagach',
      searchDescription: 'Wyszukuj po opisach',
      tags: []
    }
  }

  updateByDescription = (event) => {
    const val = event.target.value;

    this.props.updateByDescription(val)
  }

  updateByTags = (e) => {
    const val = e.target.value;
    if (e.key === 'Enter' && val) {
      if (this.state.tags.find(tag => tag.toLowerCase() === val.toLowerCase())) {
        return;
      }
      this.setState({ tags: [...this.state.tags, val] });
      this.tagInput.value = null;
      this.props.updateByTags(this.state.tags)
    }
    else if (e.key === 'Backspace' && !val) {
      this.removeTag(this.state.tags.length - 1);
      this.props.updateByTags(this.state.tags)
    }    
  }

  removeTag = (i) => {
    const newTags = [...this.state.tags];
    newTags.splice(i, 1);
    this.setState({ tags: newTags });
  }

  render() {
    return (
      <div>
        <input type="text"
          placeholder='Wyszukuj po opisach'
          onChange={this.updateByDescription} />
        <div className="input-tag">
          <ul className="input-tag__tags">

            {this.state.tags.map((tag, i) => (
              <li key={tag}>
                {tag}
                <button type="button" onClick={() => { this.removeTag(i); }}>+</button>
              </li>
            ))}

            <li className="input-tag__tags__input">
              <input type="text" placeholder='Wyszukuj po tagach' onSubmit={this.updateByTags} ref={c => { this.tagInput = c; }} />
            </li>

          </ul>

        </div>
      </div>
    )
  }
}
